/* -- Init start -- */

/* who am I? */
const ident = 'tarot';
lockPidFile(ident);
setPingListener(ipc, ident, 'init');

/* -- the card shuffling and reading parts -- */
function shuffled_deck(){let i,o=Array.from({length:78},(r,o)=>o+1);for(let r=78;r;r--){let a=Math.floor(Math.random()*r);[o[r-1],o[a]]=[o[a],o[r-1]]}for(i=Math.floor(6*Math.random()+23);i;i--)o[i-1]*=-1;for(i=78;i;i--){let r=Math.floor(Math.random()*i);[o[i-1],o[r]]=[o[r],o[i-1]]}return o}
function i_to_c1(t){let n,e;return n=tarot_db.tarot_interpretations[Math.abs(t)],e=n.fortune_telling,punycode.ucs2.encode([n.unicode])+" "+n.name+": "+e[Math.floor(Math.random()*e.length)]}
function i_to_c3(t){let n,e;return e=0<t?(n=tarot_db.tarot_interpretations[t-1],n.meanings.light):(n=tarot_db.tarot_interpretations[-1*t-1],n.meanings.dark),punycode.ucs2.encode([n.unicode])+" "+n.name+" "+e[Math.floor(Math.random()*e.length)]}

/* -- the commands -- */
var help = [];
/* just cut the deck and show one card */
const tarot1 = (data) => {
  const request = JSON.parse(data);
  let t=shuffled_deck();
  let reply = {target: request.channel, text: i_to_c1(t[Math.floor(Math.random()*t.length)])}
  if (debug) clog.debug(`Sending answer to: ${request.replyTo}.outgoingMessage`, reply);
  ipc.publish(`${request.replyTo}.outgoingMessage`, JSON.stringify(reply));
};
help.push({command: 'tarot1', descr: 'Draw one tarot card', params: []})
ipc.subscribe('tarot1.request', tarot1);

/* 3 fates; past, present, future */
const tarot3 = (data) => {
  const request = JSON.parse(data);
  let t=shuffled_deck();
  let reply = { target: request.channel,
      text: ircColor.bold("Past:")+" "+i_to_c3(t[0])+". "+
            ircColor.bold("Present:")+" "+i_to_c3(t[1])+". "+
            ircColor.bold("Future:")+" "+i_to_c3(t[2])+"."
  }
  if (debug) clog.debug(`Sending answer to: ${request.replyTo}.outgoingMessage`, reply);
  ipc.publish(`${request.replyTo}.outgoingMessage`, JSON.stringify(reply));
};
help.push({command: 'tarot3', descr: 'Three-card tarot reading', params: []})
ipc.subscribe('tarot3.request', tarot3);
help.push({command: 'tarot', descr: 'Simple tarot reading', params: []})
ipc.subscribe('tarot.request', tarot3);

