/* what do once I'm loaded */
ipc.on('start', () => {
  if (debug) clog.debug('IPC "connected"');
  clog.debug('QUACK tarot IPC "connected"');
  if (process.send) process.send('ready');
  ipc.publish(
    '_help.update', JSON.stringify({ from: ident, help: help, }),
  );
  setPingListener(ipc, ident, 'running');
});
/* what do if I'm asked to reassert my help strings */
ipc.subscribe('_help.updateRequest',()=>{
  ipc.publish('_help.update',JSON.stringify({from:ident,help:help}));
});
/* what do before I'm unloaded */
process.on("SIGINT",()=>{
  handleSIGINT(ident,ipc)
});
